package com.github.nfcforandroid.parser.ndef;import android.nfc.NdefRecord;import com.github.nfcforandroid.api.INfaParser;import com.github.nfcforandroid.api.INfaRecord;import com.github.nfcforandroid.parser.base.NfaParserAdapter;import com.github.nfcforandroid.records.factory.NfaRecordFactory;import com.github.nfcforandroid.records.ndef.UnknownRecord;/** * @author jefBinomed *  *  *         {@link INfaParser} for Unkown data. *  *         The return value is a {@link UnknownRecord} *  *         a {@link IllegalArgumentException} could be thrown if the type is not empty *  */public class UnknownParser extends NfaParserAdapter {	UnknownParser() {	}	/*	 * (non-Javadoc)	 * 	 * @see com.github.nfcforandroid.parser.base.NfaParserAdapter#parseNdef(android.nfc.NdefRecord)	 */	@Override	public INfaRecord parseNdef(NdefRecord ndefRecord) {		byte[] type = ndefRecord.getType();		if (type != null && type.length > 0) {			throw new IllegalArgumentException("Record type not expeted");		}		return NfaRecordFactory.ndefFactory().unknownRecordInstance(ndefRecord.getPayload());	}}