package com.nfa.tools.parser.ndef;import android.nfc.NdefRecord;import com.nfa.tools.api.INfaRecord;import com.nfa.tools.parser.base.NfaParserAdapter;import com.nfa.tools.records.factory.NfaRecordFactory;;public class UnknownParser extends NfaParserAdapter {	UnknownParser() {	}	@Override	public INfaRecord parseNdef(NdefRecord ndefRecord) {		byte[] type = ndefRecord.getType();		if (type != null && type.length > 0) {			throw new IllegalArgumentException("Record type not expeted");		}		return NfaRecordFactory.ndefFactory().unknownRecordInstance(ndefRecord.getPayload());	}}