package com.nfa.tools.parser.ndef;import android.nfc.NdefRecord;import com.nfa.tools.api.INfaParser;import com.nfa.tools.api.INfaRecord;import com.nfa.tools.parser.base.NfaParserAdapter;import com.nfa.tools.records.factory.NfaRecordFactory;import com.nfa.tools.records.ndef.UnsupportedRecord;/** * @author jefBinomed *  *         {@link INfaParser} for Unsupported data. *  *         The return value is a {@link UnsupportedRecord} */public class UnsupportedParser extends NfaParserAdapter {	UnsupportedParser() {	}	/*	 * (non-Javadoc)	 * 	 * @see com.nfa.tools.parser.base.NfaParserAdapter#parseNdef(android.nfc.NdefRecord)	 */	@Override	public INfaRecord parseNdef(NdefRecord ndefRecord) {		return NfaRecordFactory.ndefFactory().unsupportedRecord(ndefRecord);	}}